#!/usr/bin/env bash

if [ -f .env.secrets ]
then
  export $(cat .env.secrets | xargs) 
else
    echo "Please set your .env.secrets file"
    exit 1
fi

# Initialize variables
GAS_PRICE=""
PRIORITY_GAS_PRICE=""

# Function to display usage
usage() {
    echo "Usage: $0 --gas-price <gas price> --priority-gas-price <priority gas price>"
    exit 1
}

# Process arguments
while [[ "$#" -gt 0 ]]; do
    case $1 in
        --gas-price) GAS_PRICE=$(($2 * 1000000000)); shift ;;
        --priority-gas-price) PRIORITY_GAS_PRICE=$(($2 * 1000000000)); shift ;;
        *) usage ;;
    esac
    shift
done

# Check if all parameters are set
if [ -z "$GAS_PRICE" ] || [ -z "$PRIORITY_GAS_PRICE" ]; then
    usage
fi

# Check if the user's balance is sufficient - required .1 ETH or equivalent
USER_BALANCE=$(cast balance $USER_ADDRESS --rpc-url $RPC_URL)

if [ $USER_BALANCE -lt 100000000000000000 ]; then
    echo "Insufficient balance. Please top up your account."
    exit 1
fi

# Identify if any prerequisites are missing:
# 1. Keyless Create2 Address (0x7A0D94F55792C434d74a40883C6ed8545E406D12)
KEYLESS_CREATE2_BYTECODE=$(cast code 0x7A0D94F55792C434d74a40883C6ed8545E406D12 --rpc-url $RPC_URL)
if [ "$KEYLESS_CREATE2_BYTECODE" = "0x" ]; then
    # Send .01 ETH to keyless deployer
    cast send 0x4c8D290a1B368ac4728d83a9e8321fC3af2b39b1 --value 10000000000000000 --gas-price $GAS_PRICE --priority-gas-price $PRIORITY_GAS_PRICE --rpc-url $RPC_URL --private-key $PK

    # Create Keyless Create2 Address
    cast publish --rpc-url ${RPC_URL} 0xf87e8085174876e800830186a08080ad601f80600e600039806000f350fe60003681823780368234f58015156014578182fd5b80825250506014600cf31ba02222222222222222222222222222222222222222222222222222222222222222a02222222222222222222222222222222222222222222222222222222222222222    
fi

# 2. Inefficient Create2 Address (0xcfA3A7637547094fF06246817a35B8333C315196) - deployed in script
INEFFICIENT_CREATE2_BYTECODE=$(cast code 0xcfA3A7637547094fF06246817a35B8333C315196 --rpc-url $RPC_URL)
if [ "$INEFFICIENT_CREATE2_BYTECODE" = "0x" ]; then
    cast send --rpc-url ${RPC_URL} --private-key ${PK} 0x7a0d94f55792c434d74a40883c6ed8545e406d12 
fi
# 3. Immutable Create2 Factory (0x0000000000ffe8b47b3e2130213b802212439497) - deployed in script
IMMUTABLE_CREATE2_FACTORY_BYTECODE=$(cast code 0x0000000000ffe8b47b3e2130213b802212439497 --rpc-url $RPC_URL)
if [ "$IMMUTABLE_CREATE2_FACTORY_BYTECODE" = "0x" ]; then
    cast send --rpc-url ${RPC_URL} --private-key ${PK} 0xcfa3a7637547094ff06246817a35b8333c315196 
fi


echo ""
echo "============= DEPLOYING PERMITC ============="

echo "Gas Price (wei): $GAS_PRICE"
echo "Priority Gas Price (wei): $PRIORITY_GAS_PRICE"
echo "RPC URL: $RPC_URL"
echo "User Address: $USER_ADDRESS"
echo "User Balance: $USER_BALANCE"
read -p "Do you want to proceed? (yes/no) " yn

case $yn in 
  yes ) echo ok, we will proceed;;
  no ) echo exiting...;
    exit;;
  * ) echo invalid response;
    exit 1;;
esac

forge script script/DeployDeterministicPermitC.s.sol:DeployDeterministicPermitC \
    --gas-price $GAS_PRICE \
    --priority-gas-price $PRIORITY_GAS_PRICE \
    --rpc-url $RPC_URL \
    --broadcast \
    --optimizer-runs 20000 \
    --verify